package chap3;

public class Test27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for문
		for(int i = 10; i<10; i++) {
			System.out.println("for Ok");
		}
		//while 문
		int j = 10;
		while(j<10) {
			System.out.println("while ok");
			j++;
		}
		//do~while 문
		int k = 10;
		do {
			System.out.println("do-while ok");
			k++;
		} while(k<10);

	}

}
